import { useBox, usePlane } from "@react-three/cannon";
import { Interactive, RayGrab } from "@react-three/xr";
import { useState } from "react";
import { Plane } from "three";
import { OrbitControls, Sky } from "three-stdlib";

export default function Box() {
  const [isHovered, setIsHovered] = useState(false);

  function Cube({ position, args = [1, 1, 1] }) {
    const [boxRef] = useBox(() => ({ position, mass: 1, args }));
    return (
      <Box ref={boxRef} args={args} castShadow>
        <meshStandardMaterial />
      </Box>
    );
  }

  function Scene() {
    const [floorRef] = usePlane(() => ({
      args: [10, 10],
      rotation: [-Math.PI / 2, 0, 0],
      position: [0, 1, 0],
      type: "Static",
    }));
    return (
      <>
        <Sky />
        <Plane ref={floorRef} args={[10, 10]} recieveShadow>
          <meshStandardMaterial attach="material" color="#fff" />
        </Plane>
        {[...Array(7)].map((_, i) => (
          <Cube key={i} position={[0, 1.1 + 0.1 * i, -0.5]} />
        ))}
        <OrbitControls />
        <ambientLight intensity={0.5} />
        <spotLight
          position={[1, 8, 1]}
          angle={0.3}
          penumbra={1}
          intensity={1}
          castShadow
        />
      </>
    );
  }

  return (
    <RayGrab>
      <Interactive
        onHover={() => setIsHovered(true)}
        onBlur={() => setIsHovered(false)}
      >
        <Scene />
      </Interactive>
    </RayGrab>
  );
}
