import {
  VRCanvas,
  RayGrab,
  DefaultXRControllers,
  Interactive,
} from "@react-three/xr";
import {
  Box,
  OrbitControls,
  Plane,
  Sky,
  useMatcapTexture,
} from "@react-three/drei";
import { usePlane, useBox, Physics } from "@react-three/cannon";
import "./App.css";
import { useState } from "react";

