function Box({ color, size, scale, children, kinematic, position, ...rest }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position} {...rest}>
        <boxBufferGeometry attach="geometry" args={size} />
        <meshPhongMaterial attach="material" color={color} />
        {children}
      </mesh>
    </RayGrab>
  );
}

