import {
  VRCanvas,
  RayGrab,
  DefaultXRControllers,
  Interactive,
  useXREvent,
} from "@react-three/xr";
import {
  Box,
  OrbitControls,
  Plane,
  Sky,
  useMatcapTexture,
} from "@react-three/drei";
import { usePlane, useBox, Physics } from "@react-three/cannon";
import "./App.css";
import { useState } from "react";

function Cube({ position, args = [0.06, 0.06, 0.06] }) {
  const [boxRef] = useBox(() => ({ position, mass: 1, args }));
  const [tex] = useMatcapTexture("C7C0AC_2E181B_543B30_6B6270");
  return (
    <Box ref={boxRef} args={args} castShadow>
      <meshMatcapMaterial attach="material" matcap={tex} />
    </Box>
  );
}

function Scene({ setPaused, setValue }) {
  const [floorRef] = usePlane(() => ({
    args: [10, 10],
    rotation: [-Math.PI / 2, 0, 0],
    position: [0, 1, 0],
    type: "Static",
  }));

  return (
    <>
      <Sky />
      <Plane ref={floorRef} args={[10, 10]} receiveShadow>
        <meshStandardMaterial attach="material" color="#fff" />
      </Plane>
      {/* <Hands />
      <HandsReady>
        <HandsColliders />
      </HandsReady> */}
      <DefaultXRControllers />
      {[...Array(7)].map((_, i) => (
        <RayGrab key={i}>
          <Interactive
            onSelect={() => setPaused(true)}
            onSelectEnd={(e) => {
              setPaused(false);
              setValue(e.contact.impactVelocity);
            }}
            // onHover={() => setIsHovered(true)}
            // onBlur={() => setIsHovered(false)}
          >
            <Cube key={i} position={[0, 1.1 + 0.1 * i, -0.5]} />
          </Interactive>
        </RayGrab>
      ))}
      <OrbitControls />
      <ambientLight intensity={0.5} />
      <spotLight
        position={[1, 8, 1]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
      />
    </>
  );
}

const App = () => {
  const [paused, setPaused] = useState(false);
  const [value, setValue] = useState(-4);

  // window.addEventListener("mousemove", (e) => {
  //   console.log(e);
  // });
  return (
    <>
      <button onClick={() => setPaused(true)}>Pause</button>
      <button onClick={() => setPaused(false)}>Go</button>
      <VRCanvas shadowmap="true" vr="true">
        <Physics
          isPaused={paused}
          // iterations={20}
          tolerance={0.0001}
          defaultContactMaterial={{
            friction: 0.9,
            restitution: 0.7,
            contactEquationStiffness: 1e7,
            contactEquationRelaxation: 1,
            frictionEquationStiffness: 1e7,
            frictionEquationRelaxation: 2,
          }}
          shouldInvalidate={true}
          gravity={[0, value, 0]}
          allowSleep={true}
          // Adjust to the headset refresh rate
          // step={1 / 60}
        >
          <Scene setPaused={setPaused} setValue={setValue} />
        </Physics>
      </VRCanvas>
    </>
  );
};

export default App;

//@ts-ignore
