import {
  VRCanvas,
  RayGrab,
  DefaultXRControllers,
  Interactive,
  useXREvent,
} from "@react-three/xr";
import {
  Box,
  OrbitControls,
  Plane,
  Sky,
  useMatcapTexture,
} from "@react-three/drei";
import { usePlane, useBox, Physics } from "@react-three/cannon";
import "./App.css";
import { useState } from "react";

function Cube({ position, args = [0.06, 0.06, 0.06] }) {
  const [boxRef] = useBox(() => ({ position, mass: 10, args }));
  const [tex] = useMatcapTexture("C7C0AC_2E181B_543B30_6B6270");
  return (
    <Box ref={boxRef} args={args} castShadow needsUpdate={true}>
      <meshMatcapMaterial attach="material" matcap={tex} needsUpdate={true} />
    </Box>
  );
}

function Scene({ setPaused, paused }) {
  const [floorRef] = usePlane(() => ({
    args: [10, 10],
    rotation: [-Math.PI / 2, 0, 0],
    position: [0, 1, 0],
    type: "Static",
  }));

  return (
    <>
      <Sky />
      <Plane ref={floorRef} args={[10, 10]} receiveShadow>
        <meshStandardMaterial attach="material" color="#fff" />
      </Plane>
      <DefaultXRControllers />
      {[...Array(2)].map((_, i) => (
        <RayGrab key={i}>
          <Interactive
            onSelect={() => setPaused(!paused)}
            onBlur={() => setPaused(!paused)}
            // onHover={() => setIsHovered(true)}
            // onBlur={() => setIsHovered(false)}
          >
            <Physics
              isPaused={paused}
              // iterations={20}
              tolerance={0.0001}
              gravity={[0, -2, 0]}
              // Adjust to the headset refresh rate
              // step={1 / 60}
            >
              <Cube
                key={i}
                position={[0, 1.1 + 0.1 * i, -0.5]}
                needsUpdate={true}
              />
              <Plane ref={floorRef} args={[10, 10]} receiveShadow>
                <meshStandardMaterial attach="material" color="#fff" />
              </Plane>
            </Physics>
          </Interactive>
        </RayGrab>
      ))}
      <OrbitControls />
      <ambientLight intensity={0.5} />
      <spotLight
        position={[1, 8, 1]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
      />
    </>
  );
}

export default function App() {
  const [paused, setPaused] = useState(false);

  // window.addEventListener("mousemove", (e) => {
  //   console.log(e);
  // });
  return (
    <>
      <button onClick={() => setPaused(true)}>Pause</button>
      <button onClick={() => setPaused(false)}>Go</button>
      <VRCanvas shadowmap="true" vr="true">
        <Physics
          isPaused={paused}
          // iterations={20}
          tolerance={0.0001}
          defaultContactMaterial={{
            friction: 0.9,
            restitution: 0.7,
            contactEquationStiffness: 1e7,
            contactEquationRelaxation: 1,
            frictionEquationStiffness: 1e7,
            frictionEquationRelaxation: 2,
          }}
          gravity={[0, -2, 0]}
          // Adjust to the headset refresh rate
          // step={1 / 60}
        >
          <Scene setPaused={setPaused} paused={paused} />
        </Physics>
      </VRCanvas>
    </>
  );
}
