import { VRCanvas, RayGrab, DefaultXRControllers } from "@react-three/xr";
import { OrbitControls, Sky, Text } from "@react-three/drei";
import "./App.css";
import { useRef } from "react";

function Box({ size, scale, children, position }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position} castShadow="true">
        <boxBufferGeometry attach="geometry" args={size} />
        <meshNormalMaterial />
        {children}
      </mesh>
    </RayGrab>
  );
}

function Floor() {
  return (
    <mesh receiveShadow="true" rotation-x={Math.PI * -0.5}>
      <planeBufferGeometry attach="geometry" args={[20, 20]} />
      <meshStandardMaterial
        attach="material"
        color="#fff"
        metalness={0.4}
        roughness={0.3}
      />
    </mesh>
  );
}

function Light() {
  const ref = useRef();

  // ref.shadow.mapSize.width = 1024;
  // ref.shadow.mapSize.height = 1024;
  ref.shadow.camera.fov = 30;
  ref.shadow.camera.near = 1;
  ref.shadow.camera.far = 5;

  ref.return(
    <>
      <ambientLight intensity={0.5} />
      <spotLight
        ref={ref}
        position={[1, 8, 1]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
      />
    </>
  );
}

export default function App() {
  return (
    <>
      <VRCanvas vr="true">
        <Sky />
        <Floor />
        <DefaultXRControllers />
        {[...Array(4)].map((_, i) => (
          <Box key={i} size={[0.4, 0.4, 0.4]} position={[0, i / 1.5 + 1, 0]}>
            <Text
              position={[0, 0, 0.21]}
              fontSize={0.07}
              color="#000"
              anchorX="center"
              anchorY="middle"
            >
              Move Me
            </Text>
          </Box>
        ))}
        <OrbitControls />
        <Light />
      </VRCanvas>
    </>
  );
}
