import React, {
  useState,
  useCallback,
  useRef,
  useEffect,
  useMemo,
  ReactNode,
} from "react";
import {
  VRCanvas,
  Hover,
  DefaultXRControllers,
  useXR,
  XRHandedness,
  XREvent,
  useXREvent,
  XRInteractionEvent,
} from "@react-three/xr";
import { Sky, Text } from "@react-three/drei";
import "./App.css";
import { Object3D, Group, Matrix4, Vector3, Quaternion, Euler } from "three";
import { createPortal, useResource, useFrame } from "react-three-fiber";

function Box({ color, size, scale, children, kinematic, position, ...rest }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position} {...rest}>
        <boxBufferGeometry attach="geometry" args={size} />
        <meshPhongMaterial attach="material" color={color} />
        {children}
      </mesh>
    </RayGrab>
  );
}

export function RayGrab({ children }) {
  const { addInteraction } = useXR();

  const hoveredHandedness =
    (useRef < Set < XRHandedness) | (undefined >> new Set());
  const grabbingController = useRef();
  const groupRef = useRef();

  const previousTransform = (useRef < Matrix4) | (undefined > undefined);

  const onEnd = useCallback((_) => {
    grabbingController.current = undefined;
    previousTransform.current = undefined;
  }, []);

  const onStart = useCallback((e) => {
    if (hoveredHandedness.current.has(e.controller.inputSource?.handedness)) {
      grabbingController.current = e.controller.controller;
      previousTransform.current = new Matrix4().getInverse(
        e.controller.controller.matrixWorld
      );
    }
  }, []);

  useXREvent("selectstart", onStart);
  useXREvent("selectend", onEnd);

  useFrame(() => {
    if (!grabbingController.current || !previousTransform.current) {
      return;
    }

    const controller = grabbingController.current;
    const group = groupRef.current;

    group.applyMatrix4(previousTransform.current);
    group.applyMatrix4(controller.matrixWorld);
    group.updateWorldMatrix(false, true);

    previousTransform.current.getInverse(controller.matrixWorld);
  });

  useEffect(() => {
    addInteraction(groupRef.current, "onHover", (e) => {
      hoveredHandedness.current.add(e.controller.inputSource?.handedness);
    });
    addInteraction(groupRef.current, "onBlur", (e) => {
      hoveredHandedness.current.delete(e.controller.inputSource?.handedness);
    });
  }, [addInteraction]);

  return <group ref={groupRef}>{children}</group>;
}

function Button(props) {
  return (
    <Box size={[0.4, 0.1, 0.1]} {...props}>
      <Text
        position={[0, 0, 0.06]}
        fontSize={0.05}
        color="#000"
        anchorX="center"
        anchorY="middle"
      >
        Hello react-xr!
      </Text>
    </Box>
  );
}

export function App() {
  return (
    <VRCanvas>
      <Sky />
      <pointLight position={[10, 10, 10]} />
      <DefaultXRControllers />
      <Button position={[0, 1.5, -1]} />
    </VRCanvas>
  );
}
