import { Interactive, RayGrab } from "@react-three/xr";
import { useState } from "react";

export default function Box() {
  const [hovered, isHovered] = useState(null);

  function MyBox() {
    return (
      <mesh>
        <boxBufferGeometry args={[1, 1, 1]} />
        <meshBasicMaterial color={isHovered ? "#ffdb57" : "#6a1b9a"} />
      </mesh>
    );
  }

  return (
    <RayGrab>
      <Interactive>
        <MyBox />
      </Interactive>
    </RayGrab>
  );
}
