import {
  VRCanvas,
  RayGrab,
  DefaultXRControllers,
  Interactive,
  useXREvent,
  useController,
} from "@react-three/xr";
import {
  Box,
  OrbitControls,
  Plane,
  Sky,
  useMatcapTexture,
} from "@react-three/drei";
import { usePlane, useBox, Physics } from "@react-three/cannon";
import "./App.css";
import { useState } from "react";
import create from "zustand";
import { useFrame } from "@react-three/fiber";
import * as THREE from "three";

function Cube({ position, args = [0.06, 0.06, 0.06] }) {
  const [boxRef] = useBox(() => ({ position, mass: 10, args }));
  const [tex] = useMatcapTexture("C7C0AC_2E181B_543B30_6B6270");
  return (
    <Box ref={boxRef} args={args} castShadow needsUpdate={true}>
      <meshMatcapMaterial attach="material" matcap={tex} needsUpdate={true} />
    </Box>
  );
}

function Controller() {
  const [useStore] = create((set) => ({
    welcome: true,
    api: {
      controller(velocity) {},
    },
  }));

  const { controller } = useStore((state) => state.api);

  const rightController = useController("right");
  const [ref, api] = useBox(() => ({
    type: "Kinematic",
    args: [0.17, 0.05, 0.175],
    onCollide: (e) => controller(e.contact.velocity),
  }));

  useFrame((state) => {
    if (!rightController) {
      return;
    }
    const { grip: controller } = rightController;
    const forward = new THREE.Vector3(0, 0, -0.175);
    forward.applyQuaternion(controller.quaternion);
    const position = new THREE.Vector3().copy(controller.position).add(forward);
    api.position.set(position.x, position.y, position.z);
    api.rotation.set(
      controller.rotation.x,
      controller.rotation.y,
      controller.rotation.z
    );
  });
}

function Scene({ setPaused, paused }) {
  const [useStore] = create((set) => ({
    welcome: true,
    api: {
      controller(velocity) {},
    },
  }));

  const [floorRef] = usePlane(() => ({
    args: [10, 10],
    rotation: [-Math.PI / 2, 0, 0],
    position: [0, 1, 0],
    type: "Static",
  }));

  const controller = useController("right");

  const [ref, api] = useBox(() => ({
    type: "Kinematic",
    args: [0.17, 0.05, 0.175],
    onCollide: (e) => controller(e.contact.velocity),
  }));

  useFrame((state) => {
    if (!controller) {
      return;
    }
    const { grip: controller } = controller;
    const forward = new THREE.Vector3(0, 0, -0.175);
    forward.applyQuaternion(controller.quaternion);
    const position = new THREE.Vector3().copy(controller.position).add(forward);
    api.position.set(position.x, position.y, position.z);
    api.rotation.set(
      controller.rotation.x,
      controller.rotation.y,
      controller.rotation.z
    );
  });

  return (
    <>
      <Sky />
      <Plane ref={floorRef} args={[10, 10]} receiveShadow>
        <meshStandardMaterial attach="material" color="#fff" />
      </Plane>
      <DefaultXRControllers />
      {[...Array(2)].map((_, i) => (
        <RayGrab key={i}>
          <Interactive
          // onSelect={() => setPaused(!paused)}
          // onBlur={() => setPaused(!paused)}
          // onHover={() => setIsHovered(true)}
          // onBlur={() => setIsHovered(false)}
          >
            <Cube
              key={i}
              position={
                controller
                  ? [
                      controller.grip.position.x,
                      controller.grip.position.y + 0.5,
                      controller.grip.position.z - 0.1,
                    ]
                  : [0, 1.1 + 0.1 * i, -0.5]
              }
              needsUpdate={true}
            />
          </Interactive>
        </RayGrab>
      ))}
      <OrbitControls />
      <ambientLight intensity={0.5} />
      <spotLight
        position={[1, 8, 1]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
      />
    </>
  );
}

export default function App() {
  const [paused, setPaused] = useState(false);

  // window.addEventListener("mousemove", (e) => {
  //   console.log(e);
  // });
  return (
    <>
      <button onClick={() => setPaused(true)}>Pause</button>
      <button onClick={() => setPaused(false)}>Go</button>
      <VRCanvas shadowmap="true" vr="true">
        <Physics
          isPaused={paused}
          // iterations={20}
          tolerance={0.0001}
          defaultContactMaterial={{
            friction: 0.9,
            restitution: 0.7,
            contactEquationStiffness: 1e7,
            contactEquationRelaxation: 1,
            frictionEquationStiffness: 1e7,
            frictionEquationRelaxation: 2,
          }}
          gravity={[0, -2, 0]}
          // Adjust to the headset refresh rate
          // step={1 / 60}
        >
          <Scene setPaused={setPaused} paused={paused} />
          <Controller />
        </Physics>
      </VRCanvas>
    </>
  );
}
