import { VRCanvas, RayGrab, DefaultXRControllers } from "@react-three/xr";
import { OrbitControls, Sky, Text } from "@react-three/drei";
import "./App.css";

function Box({ size, scale, children, position }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position}>
        <boxBufferGeometry attach="geometry" args={size} />
        <meshNormalMaterial />
        {children}
      </mesh>
    </RayGrab>
  );
}

export default function App() {
  return (
    <>
      <VRCanvas shadowmap="true" vr="true">
        <Sky />
        <DefaultXRControllers />
        {[...Array(3)].map((i) => (
          <Box key={i} size={[0.5, 0.5, 0.5]} position={[0, i, -0.5]}>
            <Text
              position={[0, 0, 0.26]}
              fontSize={0.07}
              color="#000"
              anchorX="center"
              anchorY="middle"
            >
              Move Me
            </Text>
          </Box>
        ))}
        <OrbitControls />
        <ambientLight intensity={0.5} />
        <spotLight
          position={[1, 8, 1]}
          angle={0.3}
          penumbra={1}
          intensity={1}
          castShadow
        />
      </VRCanvas>
    </>
  );
}
