import {
  VRCanvas,
  RayGrab,
  DefaultXRControllers,
  Interactive,
} from "@react-three/xr";
import {
  OrbitControls,
  Plane,
  Sky,
  Text,
  useMatcapTexture,
} from "@react-three/drei";
import { usePlane, useBox, Physics } from "@react-three/cannon";
import "./App.css";
import { useState } from "react";

// function Cube({ position, args = [0.06, 0.06, 0.06] }) {
//   // const [boxRef] = useBox(() => ({ position, mass: 1, args }));
//   const [tex] = useMatcapTexture("C7C0AC_2E181B_543B30_6B6270");
//   return (
//     <Box ref={boxRef} args={args} castShadow>
//       <meshMatcapMaterial attach="material" matcap={tex} />
//     </Box>
//   );
// }

function Cube({ size, scale, kinematic, position, ...rest }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position} {...rest}>
        <boxBufferGeometry attach="geometry" args={size} />
        <meshNormalMaterial />
      </mesh>
    </RayGrab>
  );
}

export default function App() {
  const [paused, setPaused] = useState(false);

  // window.addEventListener("mousemove", (e) => {
  //   console.log(e);
  // });
  return (
    <>
      <VRCanvas shadowmap="true" vr="true">
        <Sky />
        <DefaultXRControllers />
        {[...Array(3)].map((_, i) => (
          <Box key={i} size={[1, 0.5, 0.5]} position={[0, i, -0.5]}>
            <Text
              position={[1, 1, 0.06]}
              fontSize={0.05}
              color="#000"
              anchorX="center"
              anchorY="middle"
            >
              Move Me
            </Text>
          </Box>
        ))}
        <OrbitControls />
        <ambientLight intensity={0.5} />
        <spotLight
          position={[1, 8, 1]}
          angle={0.3}
          penumbra={1}
          intensity={1}
          castShadow
        />
      </VRCanvas>
    </>
  );
}

function Box({ color, size, scale, children, kinematic, position, ...rest }) {
  return (
    <RayGrab>
      <mesh scale={scale} position={position} {...rest}>
        <boxBufferGeometry attach="geometry" args={size} />
        <meshNormalMaterial attach="material" color={color} />
        {children}
      </mesh>
    </RayGrab>
  );
}
