          <Cube key={i} position={[0, 1.1 + 0.1 * i, -0.5]} />
        </RayGrab>
      ))}
      <OrbitControls />
      <ambientLight intensity={0.5} />
      <spotLight
        position={[1, 8, 1]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
      />
    </>
  );
}

const App = () => (
  <VRCanvas shadowmap="true" vr="true">
    <Physics
      gravity={[0, -2, 0]}
      iterations={20}
      defaultContactMaterial={{
        friction: 0.09,
      }}
    >
      <Scene />
    </Physics>
  </VRCanvas>
);

export default App;

